<?php

function cbaffmach_traffic_stumbleupon( $post_id, $traffic, $settings ) {
	$category = ( isset( $settings->category ) && !empty( $settings->category ) ) ? $settings->category : 'News(General)';
	$res = cbaffmach_share_stumbleupon( $category, cbaffmach_get_the_excerpt( $post_id ), get_permalink( $post_id ) );
	if( $res )
		cbaffmach_log_traffic( $post_id, CBAFFMACH_TRAFFIC_STUMBLEUPON );
}

function cbaffmach_share_stumbleupon( $category, $text, $post_url ) {
	$stumbleupon_settings = cbaffmach_get_settings( array( 'social', 'stumbleupon' ) );

	if ( ! isset( $stumbleupon_settings['email'] ) || empty( $stumbleupon_settings['email'] ) || ! isset( $stumbleupon_settings['pass'] ) || empty( $stumbleupon_settings['pass'] ) ) {
		cbaffmach_debug( 'Error with Stumbleupon Credentials', 'error' );
		return false;
	}
	cbaffmach_include_nextgen();
	require_once CBAFFMACH_DIR.'/lib/libs/pinterest/inc-cl/su.api.php';

	$email = $stumbleupon_settings['email'];
	$password = $stumbleupon_settings['pass'];

	$nxs_gCookiesArr = cbaffmach_get_saved_stumbleupon_login();

	$nt         = new nxsAPI_SU();
	$nt->ck     = $nxs_gCookiesArr;
	$loginError = $nt->connect( $email, $password );
// 	echo '<pre>';
// print_r($loginError);
// 	echo '</pre>';
	if ( !$loginError ) {
	  	cbaffmach_save_stumbleupon_login($nt->ck);
		$return = $nt->post($text, $post_url, $category );
		cbaffmach_debug( 'Shared on Stumbleupon', 'notice' );
		return true;
	  // var_dump($return);
	}
	else {
		cbaffmach_debug( 'Stumbleupon - error with login', 'error' );
	}
	return false;
}

function cbaffmach_get_saved_stumbleupon_login() {
  $pnTempLogin = get_option( 'cbaffmach_su_temp_login' );
  // $pnTempLogin = get_post_meta('111111113', 'cbaffmach_su_temp_login', true);
  if (empty($pnTempLogin)) return null;
  return unserialize($pnTempLogin);
}

function cbaffmach_save_stumbleupon_login($obj) {
	update_option( 'cbaffmach_su_temp_login', serialize($obj) );
  // update_post_meta('111111113', 'cbaffmach_su_temp_login', serialize($obj));
}

